mapper.select:
select * from CLASS where 1 = 1
and LARGE_CATEGORY in
#{item}
and ADDRESS in
#{item}

SELECT CLS.NAME, CLS.CITY, CLS.ADDRESS, USR.POINT
FROM CLASS CLS, CLASS_JOIN CLJ, USER USR
WHERE CLJ.USER_ID = #{email}
AND CLJ.CLASS_ID = CLS.ID
AND USR.EMAIL = #{email}

SELECT CLASS.ID, CLASS.NAME, CLASS.LARGE_CATEGORY, CLASS.SMALL_CATEGORY, CLASS.CITY, CLASS.ADDRESS, CLASS.MAX_MEMBER, CLASS.DETAIL_CONTENTS, CLASS.PHOTO_PATH 
FROM CLASS, USER
WHERE USER.EMAIL = #{email}
AND CLASS.OWNER_ID = #{email}
AND USER.EMAIL = CLASS.OWNER_ID

SELECT CLS.NAME
FROM CLASS_JOIN CLJ, CLASS CLS, USER USR
WHERE CLJ.USER_ID = #{email}
AND CLJ.CLASS_ID = CLS.ID
AND USR.EMAIL = #{email}
AND CLS.OPEN_DATE = #{date}
